SELECT TimeComparisons.TimeDifference, TimeComparisons.StartPoint, DateAdd("h",([TimeDifference]),[StartPoint]) AS Other, DateAdd("h",1,[TimeComparisons].[StartPoint]) AS [+1], DateAdd("h",(1+[TimeDifference]),[StartPoint]) AS [Other +1], DateAdd("h",2,[TimeComparisons].[StartPoint]) AS [+2], DateAdd("h",(2+[TimeDifference]),[StartPoint]) AS [Other +2], DateAdd("h",3,[TimeComparisons].[StartPoint]) AS [+3], DateAdd("h",(3+[TimeDifference]),[StartPoint]) AS [Other +3], DateAdd("h",4,[TimeComparisons].[StartPoint]) AS [+4], DateAdd("h",(4+[TimeDifference]),[StartPoint]) AS [Other +4], DateAdd("h",5,[TimeComparisons].[StartPoint]) AS [+5], DateAdd("h",(5+[TimeDifference]),[StartPoint]) AS [Other +5], DateAdd("h",6,[TimeComparisons].[StartPoint]) AS [+6], DateAdd("h",(6+[TimeDifference]),[StartPoint]) AS [Other +6], DateAdd("h",7,[TimeComparisons].[StartPoint]) AS [+7], DateAdd("h",(7+[TimeDifference]),[StartPoint]) AS [Other +7], DateAdd("h",8,[TimeComparisons].[StartPoint]) AS [+8], DateAdd("h",(8+[TimeDifference]),[StartPoint]) AS [Other + 8]
FROM TimeComparisons;

